        if let gr = getGroupEntry(of: egid) {
          print("(\(gr.name))", terminator: "")
        }
      }
    }
    var lastgid : Int? = nil
    var first = true
    for gid in groups {
      if (lastgid == gid) {
        first = false
        continue;
      }
      if first {
