import Foundation
import shared

let DEFFILEMODE = S_IRUSR|S_IWUSR|S_IRGRP|S_IWGRP|S_IROTH|S_IWOTH


@main final class tee : ShellCommand {
  struct FDs {
    var fd: Int32
    var name: String
  }
  var head: [FDs] = []
  
  func add(_ fd: Int32, _ name: String) {
    head.append(FDs(fd: fd, name: name))
  }
  
  var usage = "usage: tee [-ai] [file ...]"

  var args : [String] = []
//  var exitval: Int = 0
  let BSIZE = 8 * 1024
  
  var append = false

  func parseOptions() throws(CmdErr) {
    
    let go = BSDGetopt("ai")
    while let (ch, _) = try go.getopt() {
      switch(ch) {
      case "a":
        append = true
        break
      case "i":
        signal(SIGINT, SIG_IGN)
        break
      case "?":
        fallthrough
      default:
        throw CmdErr(1)
      }
    }
    
    args = go.remaining
  }
  
  func runCommand() throws(CmdErr) {
    var buf = [Int8](repeating: 0, count: BSIZE)
    
    add(STDOUT_FILENO, "stdout")
    
    for arg in args {
      let fd = open(arg, append ?
                    O_WRONLY|O_CREAT|O_APPEND :
                      O_WRONLY|O_CREAT|O_TRUNC, DEFFILEMODE)
      if fd < 0 {
        // warn(arg)
        throw CmdErr(1, arg)
//        exitval = 1
      } else {
        add(fd, arg)
      }
    }
    
   try withUnsafeMutablePointer(to: &buf) { (bufp) throws(CmdErr) in
      while true {
        let rval = read(STDIN_FILENO, bufp, BSIZE)
        if rval == 0 { break }
        if rval < 0 {
          err(1, "read")
        }
        for p in head {
          var n = rval
          
          var bp = bufp
          repeat {
            let wval = write(p.fd, bp, n)
            if wval == -1 {
              throw CmdErr(1, p.name)
//              warn( p.name)
            }
            bp += wval
            n -= wval
          } while (n != 0)
        }
      }
    }
  }
}
