import CMigration

@main final class What : ShellCommand {
  struct CommandOptions {
    var qflag = false
    var sflag = false
    var args = [String]()
  }

  var options : CommandOptions!

  func parseOptions() throws(CmdErr) -> CommandOptions {
    var opts = CommandOptions()

    let go = BSDGetopt("qs")
    while let (ch, _) = try go.getopt() {
      switch ch {
        case "q":
          opts.qflag = true
        case "s":
          opts.sflag = true
        default:
          throw CmdErr(1)
      }
    }

    opts.args = go.remaining
    return opts
  }

  func runCommand() async throws(CmdErr) {
    var file: String?
    var found = false

    do {
      if options.args.count == 0 {
        let fh = try FileDescriptor(forReading: "/dev/stdin")
        if try await search(options.sflag, options.qflag, fh) {
          found = true
        }
      } else {
        for arg in options.args {
          file = arg

          do {
            let inStream = try FileDescriptor(forReading: file!)
            if !options.qflag {
              print("\(file!):")
            }
            if try await search(options.sflag, options.qflag, inStream) {
              found = true
            }
            try? inStream.close()
          } catch {
            if !options.qflag {
              var se = FileDescriptor.standardError
              print("\(programName): \(file!): \(error)", to: &se)
            }
            continue
          }
        }
      }
    } catch let e as CmdErr {
      throw e
    } catch {
      let z = String(describing: error)
      throw CmdErr(2, z)
    }

    if !found { throw CmdErr(1, "") }
//    stdlib_h.exit(found ? 0 : 1)
  }

  var usage = "usage: what [-qs] [file ...]"

  func search(_ one: Bool, _ quiet: Bool, _ inStream: FileDescriptor) async throws -> Bool {
    var found = false

    var state = 0
    for try await b in inStream.bytes {
      let ch = Character(UnicodeScalar(b))

      switch state {
        case 0:
          if ch != "@" {
            state = 0
            continue
          }
          state = 1
        case 1:
          if ch != "(" {
            state = 0
            continue
          }
          state = 2

        case 2:
          if ch != "#" {
            state = 0
            continue
          }
          state = 3
        case 3:
          if ch != ")" {
            state = 0
            continue
          }
          state = 4

          if !quiet {
            print("\t", terminator: "")
          }
        case 4:
          if (b != 0 && ch != "\"" && ch != ">" && ch != "\\" && ch != "\n") {
            print(ch, terminator: "")
            continue
          }
          print("")
          found = true
          if one {
            break
          } else {
            state = 0
            continue
          }
        default:
          fatalError("not possible")
      }
    }
    return found
  }
}

