  func run(_ args : [String], _ opts : CommandOptions) throws(CmdErr) {
    var pid: pid_t = 0
    var rc: Int32 = 0

    var file_actions = posix_spawn_file_actions_t(bitPattern: 0)
    var inpath = _PATH_DEVNULL
    
