    rc = Darwin.posix_spawnp(&pid, args[0], &file_actions, nil, xy, Darwin.environ)
    
    if rc != 0 {
      waitchildren(jbxp[0], true, opts)
      errno = rc
//      fputs(args[0], stderr)
      throw CmdErr(rc == ENOENT ? 127 : 126, args[0])
    } else {
