  func runCommand(_ opts : CommandOptions) throws(CmdErr) {
    var pc : Character? = "\n"
    var a : String = ""
    var ibxp = [String]()
    
    while pc != nil {
      (a, pc) = try parseArgument(pc!, opts)
      if (!a.isEmpty) || (opts.zflag && opts.xflag) {
        ibxp.append(a)
      }
      if (pc == nil && ibxp.count > 0) || ibxp.count >= opts.nargs || (opts.xflag && opts.Lflag <= ibxp.count)  {
          //        try arg2(av)
          try prerun(jbxp, ibxp, kbxp, a, opts)
        ibxp = []
      }
    }
  }
  
  
  func prompt() -> PromptCases {
    var cre = Darwin.regex_t()
    var rsize: Darwin.size_t = 0
    var match: Int32 = 0
    
    let ttyfp = Darwin.fopen(_PATH_TTY, "r")
    if ttyfp != nil {
      return .two
    }
    Darwin.fputs("?...", Darwin.stderr)
    fflush(Darwin.stderr)
    let response = Darwin.fgetln(ttyfp, &rsize)
    if response == nil || Darwin.regcomp(&cre, Darwin.nl_langinfo(Darwin.YESEXPR), Darwin.REG_EXTENDED) != 0 {
      Darwin.fclose(ttyfp)
      return .zero
    }
    response![Int(rsize) - 1] = 0
    match = Darwin.regexec(&cre, response, 0, nil, 0)
    Darwin.fclose(ttyfp)
    Darwin.regfree(&cre)
    return match == 0 ? .one : .zero
  }
  
